"use strict";

var getCreatorDashBoard = function getCreatorDashBoard(req, res) {
  var cookies = req.cookies;
  if (cookies.UserName === null) {
    return res.render("./creator_dashboard.ejs", {
      userName: ''
    });
  } else {
    return res.render("./creator_dashboard.ejs", {
      userName: cookies.UserName
    });
  }
};
var getListFollower = function getListFollower(req, res) {
  var cookies = req.cookies;
  if (cookies.UserName === null) {
    return res.render("./list_follower.ejs", {
      userName: ''
    });
  } else {
    return res.render("./list_follower.ejs", {
      userName: cookies.UserName
    });
  }
};
var getAccountSetting = function getAccountSetting(req, res) {
  var cookies = req.cookies;
  if (cookies.UserName === null) {
    return res.render("./setting.ejs", {
      userName: ''
    });
  } else {
    return res.render("./setting.ejs", {
      userName: cookies.UserName
    });
  }
};
var getAccountSettingSecurity = function getAccountSettingSecurity(req, res) {
  var cookies = req.cookies;
