    }
}
let getAccountSettingSecurity = (req, res) => {
    const cookies= req.cookies;
    if(cookies.UserName===null){
        return res.render("./setting_security.ejs", {userName:''})
    }else{
        return res.render("./setting_security.ejs", {userName:cookies.UserName})
    }
}

module.exports = {
