                    "S":"-1"
                }
            },
            UpdateExpression:"SET #A= :a, #B= :b",
            TableName:"HoSoNguoiDung"
            
        }));
        const data_3 = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                },
            },
            ExpressionAttributeNames:{
                "#A":"DangKi",
