            },
            ExpressionAttributeValues:{
                ":a":{
                    "N":"0"
                },
                ":b":{
                    "S":"-1"
                }
            },
            UpdateExpression:"SET #A= :a, #B= :b",
            TableName:"HoSoNguoiDung"
        }));
        const data_8 = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                },
            },
            ExpressionAttributeNames:{
                "#A":"TrangThai",
