            },
            ExpressionAttributeValues:{
                ":a":{
                    "A":"True"
                },
                ":b":{
                    "B":NgaySinh
                }
            },
            UpdateExpression:"SET #A= :a, #B = :b",
            TableName:"HoSoNguoiDung"
        }));
        console.log("Success")
    }catch(err){
        console.log("Error")
        console.log(err)
    }
}
