                    "Gmail":{
                        "S":Gmail
                    }
                },
                "TableName":"HoSoNguoiDung", 
                "ConditionExpression":"attribute_not_exists(#TenDangNhap)",
                "ExpressionAttributeNames":{
                    "#TenDangNhap":"TenDangNhap"
                }
            }
        ));

        const data_2 = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                },
            },
            ExpressionAttributeNames:{
                "#A":"AnhBiaARN",
                "#B":"AnhDaiDienARN"
            },
            ExpressionAttributeValues:{
                ":a":{
                    "S":"-1"
                },
                ":b":{
                    "S":"-1"
                }
            },
            UpdateExpression:"SET #A= :a, #B= :b",
            TableName:"HoSoNguoiDung"
            
        }));
        const data_3 = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                },
            },
            ExpressionAttributeNames:{
                "#A":"DangKi",
                "#B":"DanhSachTheoDoi"
            },
            ExpressionAttributeValues:{
                ":a":{
                    "L":[]
                },
                ":b":{
                    "L":[]
                }
            },
            UpdateExpression:"SET #A= :a, #B= :b",
            TableName:"HoSoNguoiDung"
        }));
        const data_4 = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                },
            },
            ExpressionAttributeNames:{
                "#A":"GioiThieu",
                "#B":"Link"
            },
            ExpressionAttributeValues:{
                ":a":{
                    "S":"-1"
                },
                ":b":{
                    "L":[]
                }
            },
            UpdateExpression:"SET #A= :a, #B= :b",
            TableName:"HoSoNguoiDung"
        }));
        const data_5 = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                },
            },
            ExpressionAttributeNames:{
                "#A":"SoNguoiDangKi",
                "#B":"SoNguoiTheoDoi"
            },
            ExpressionAttributeValues:{
                ":a":{
                    "N":"0"
                },
                ":b":{
                    "N":"0"
                }
            },
            UpdateExpression:"SET #A= :a, #B= :b",
            TableName:"HoSoNguoiDung"
        }));
        const data_6 = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                },
            },
            ExpressionAttributeNames:{
                "#A":"TenKenh",
                "#B":"TheLoaiMau"
            },
            ExpressionAttributeValues:{
                ":a":{
                    "S":TenDangNhap
                },
                ":b":{
                    "S":"-1"
                }
            },
            UpdateExpression:"SET #A= :a, #B= :b",
            TableName:"HoSoNguoiDung"
        }));
        const data_7 = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                },
            },
            ExpressionAttributeNames:{
                "#A":"Tien",
                "#B":"TieuDeMacDinh"
            },
            ExpressionAttributeValues:{
                ":a":{
                    "N":"0"
                },
                ":b":{
                    "S":"-1"
                }
            },
            UpdateExpression:"SET #A= :a, #B= :b",
            TableName:"HoSoNguoiDung"
        }));
        const data_8 = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                },
            },
            ExpressionAttributeNames:{
                "#A":"TrangThai",
                "#B":"NgaySinh"
            },
            ExpressionAttributeValues:{
                ":a":{
                    "A":"True"
                },
                ":b":{
                    "B":NgaySinh
                }
            },
            UpdateExpression:"SET #A= :a, #B = :b",
            TableName:"HoSoNguoiDung"
        }));
        console.log("Success")
    }catch(err){
        console.log("Error")
        console.log(err)
    }
}
