//CRUD HoSoNguoiDung
const GetHoSoNguoiDung = async(Key) =>{
    try{
        const data = await dynamodb.send(new GetItemCommand(
            {
                "Key":{
                    "TenDangNhap": {
                        "S": Key
                    }
                },
                "TableName":"HoSoNguoiDung"
            }
        ));
        return data
    } catch (err){
        return err.message
    }
};
const CreateHoSoNguoiDung = async(TenDangNhap, Gmail, NgaySinh) =>{
    try{
        const data = await dynamodb.send(new PutItemCommand(
            {
                "Item":{
                    "TenDangNhap":{
                        "S":TenDangNhap
                    },
                    "AnhBiaARN": {
                        "S": "asdasdfs"
                    },
                    "AnhDaiDienARN": {
                        "S": "https://projectlivestreamdb.s3.ap-northeast-2.amazonaws.com/sampleperson.png"
                    },
                    "DangKi": {
                        "L": []
                    },
                    "DanhSachTheoDoi": {
                        "L": []
                    },
                    "GioiThieu": {
                        "S": ""
                    },
                    "Gmail": {
                        "S": Gmail
                    },
                    "Link": {
                        "L": []
                    },
                    "SoNguoiDangKi": {
                        "N": "0"
                    },
                    "SoNguoiTheoDoi": {
                        "N": "0"
                    },
                    "TenKenh": {
                        "S": TenDangNhap
                    },
                    "TheLoaiMau": {
                        "S": "JustChatting"
                    },
                    "Tien": {
                        "N": "0"
                    },
                    "TieuDeMacDinh": {
                        "S": "ang LiveStream"
                    },
                    "TrangThai": {
                        "BOOL": true
                    },
                    "NgaySinh":{
                        "S":NgaySinh
                    }
                },
                "TableName":"HoSoNguoiDung", 
                "ConditionExpression":"attribute_not_exists(#TenDangNhap)",
                "ExpressionAttributeNames":{
                    "#TenDangNhap":"TenDangNhap"
                }
            }
        ));
        return data
    }catch(err){
        return err.message
    }
}
const UpdateHoSoNguoiDung = async(TenDangNhap, AnhBiaARN,AnhDaiDienARN,DangKi,DanhSachTheoDoi,GioiThieu,
    Gmail,Link,SoNguoiDangKi,SoNguoiTheoDoi,TenKenh,TheLoaiMau,Tien,TieuDeMacDinh,TrangThai,NgaySinh) =>{
    try{
         const data = await dynamodb.send(new UpdateItemCommand({
            Key:{
                "TenDangNhap":{
                    "S":TenDangNhap
                }
            },
            ExpressionAttributeNames:{
                "#A":"AnhBiaARN",
                "#B":"AnhDaiDienARN",
                "#C":"DangKi",
                "#D":"DanhSachTheoDoi",
                "#E":"GioiThieu",
                "#F":"Gmail",
                "#G":"Link",
                "#H":"SoNguoiDangKi",
                "#I":"SoNguoiTheoDoi",
                "#K":"TenKenh",
                "#L":"TheLoaiMau",
                "#M":"Tien",
                "#N":"TieuDeMacDinh",
                "#O":"TrangThai",
                "#P":"NgaySinh"
            },
            ExpressionAttributeValues:{
                ":a":{
                    "S":AnhBiaARN
                },
                ":b":{
                    "S":AnhDaiDienARN
                },
                ":c":{
                    "L":DangKi
                },
                ":d":{
                    "L":DanhSachTheoDoi
                },
                ":e":{
                    "S":GioiThieu
                },
                ":f":{
                    "S":Gmail
                },
                ":g":{
                    "L":Link
                },
                ":h":{
                    "N":SoNguoiDangKi
                },
                ":i":{
                    "N":SoNguoiTheoDoi
                },
                ":k":{
                    "S":TenKenh
                },
                ":l":{
                    "S":TheLoaiMau
                },
                ":m":{
                    "N":Tien
                },
                ":n":{
                    "S":TieuDeMacDinh
                },
                ":o":{
                    "BOOL":TrangThai
                },
                ":p":{
                    "S":NgaySinh
                }
            },
            UpdateExpression:"SET #A= :a, #B= :b, #C= :c, #D= :d, #E= :e, #F= :f, #G= :g, #H= :h, #I= :i, #K= :k,#L= :l, #M= :m, #N= :n, #O= :o, #P= :p",
            TableName:"HoSoNguoiDung"
            
        }));
        return data
    }
    catch(err){
        return err.message
    }
}
