const {check} = require('express-validator');

const LoginValidate =[
    check('name').notEmpty().withMessage('Tn ti khon khng c  trng'),
    check('password').notEmpty().withMessage('Mt khu khng c  trng')
]
const RegisterValidate =[
    check('nameRegister').notEmpty().withMessage('Tn ti khon khng c  trng'),
    check('passwordRegister').notEmpty().withMessage('Mt khu khng c  trng').isLength({min:8, max:16}).withMessage('Mt khu phi t 8 n 16 k t'),
    check('confirmPassword').notEmpty().withMessage('Nhp li mt khu khng c  trng').custom((value, {req})=> {
        return value != req.body.password;
    }).withMessage("Nhp li mt khu khng ng"),
    check('email').notEmpty().withMessage('Email khng c  trng').isEmail().withMessage('Vui lng nhp dng a ch email'),
    check('birth').notEmpty().withMessage('Ngy sinh khng c  trng')
]
module.exports = {
    LoginValidate,
    RegisterValidate
}

